// frontend/src/components/SignupReducer.js

// import needed actions
import {
    CREATE_STAFF_ERROR,
    CREATE_STAFF_SUBMITTED,
    CREATE_STAFF_SUCCESS
  } from "./StaffSignupTypes";
  
  // define the initial state of the signup store
  const initialState = {
    emailError: "",
    passwordError: "",
    firstNameError: "",
    lastNameError: "",
    phoneNumberError: "",
    usernameError: "",
    positionError: "",
    rePasswordError: "",
    isSubimtted: false
  };
  
  // define how action will change the state of the store
  export const signupReducer = (state = initialState, action) => {
    switch (action.type) {
      case CREATE_STAFF_SUBMITTED:
        return {
          emailError: "",
          passwordError: "",
          firstNameError: "",
          lastNameError: "",
          phoneNumberError: "",
          usernameError: "",
          positionError: "",
          rePasswordError: "",
          isSubimtted: true
        };
      case CREATE_STAFF_ERROR:
        const errorState = {
          emailError: "",
          passwordError: "",
          firstNameError: "",
          lastNameError: "",
          phoneNumberError: "",
          usernameError: "",
          positionError: "",
          rePasswordError: "",
          isSubimtted: false
        };
        if (action.errorData.hasOwnProperty("email")) {
          errorState.emailError = action.errorData["email"];
        }
        if (action.errorData.hasOwnProperty("password")) {
          errorState.passwordError = action.errorData["password"];
        }
        if (action.errorData.hasOwnProperty("first_name")) {
            errorState.firstNameError = action.errorData["first_name"];
        }
        if (action.errorData.hasOwnProperty("last_name")) {
            errorState.lastNameError = action.errorData["last_name"];
          }
        if (action.errorData.hasOwnProperty("phone_number")) {
          errorState.phoneNumberError = action.errorData["phone_number"];
        }
        if (action.errorData.hasOwnProperty("username")) {
            errorState.usernameError = action.errorData["username"];
        }
        if (action.errorData.hasOwnProperty("position")) {
            errorState.positionError = action.errorData["position"];
        }
        if (action.errorData.hasOwnProperty("re_password")) {
            errorState.rePasswordError = action.errorData["re_password"];
        }
        return errorState;
      case CREATE_STAFF_SUCCESS:
        return {
          emailError: "",
          passwordError: "",
          firstNameError: "",
          lastNameError: "",
          phoneNumberError: "",
          usernameError: "",
          positionError: "",
          rePasswordError: "",
          isSubimtted: false
        };
      default:
        return state;
    }
  }
