import React, { Component } from "react";
import { withRouter, Link } from "react-router-dom";
import { connect } from "react-redux";

import axios from "axios";
import { setAxiosAuthToken } from "../../utils/Utils";
import {
  Alert,
  Container,
  Button,
  Row,
  Col,
  Form,
  FormControl
} from "reactstar";

class Signup extends Component {
  constructor(props) {
    super(props);
    this.state = {
      first_name: "",
      last_name: "",
      position: "",
      phone_number: "",
      email: "",
      username: "",
      password: "",
      re_password: "",
      
      first_nameError: "",
      last_nameError: "",
      positionError: "",
      phone_numberError: "",
      emailError: "",
      usernameError: "",
      passwordError: "",
      re_passwordError: "",

      status: ""
    };
  }
  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onSignupClick = () => {
    this.setState({
      first_nameError: "",
      last_nameError: "",
      positionError: "",
      phone_numberError: "",
      emailError: "",
      usernameError: "",
      passwordError: "",
      re_passwordError: "",
      status: ""
    });

    const staffData = {
      first_name: this.state.first_name,
      last_name: this.state.last_name,
      position: this.state.position,
      phone_number: this.state.phone_number,
      email: this.state.email,
      username: this.state.username,
      password: this.state.password,
      re_password: this.state.re_password
    };

    setAxiosAuthToken(""); // send request with empty token
    axios
      .post("/accounts/staff/new", staffData)
      .then(response => {
        this.setState({ status: "success" });
      })
      .catch(error => {
        if (error.response) {
          if (error.response.data.hasOwnProperty("first_name")) {
            this.setState({ first_nameError: error.response.data["first_name"] });
          }
          if (error.response.data.hasOwnProperty("last_name")) {
            this.setState({ last_nameError: error.response.data["username"] });
          }
          if (error.response.data.hasOwnProperty("position")) {
            this.setState({ positionError: error.response.data["position"] });
          }
          if (error.response.data.hasOwnProperty("phone_number")) {
            this.setState({ phone_numberError: error.response.data["phone_number"] });
          }
          if (error.response.data.hasOwnProperty("username")) {
            this.setState({ usernameError: error.response.data["username"] });
          }
          if (error.response.data.hasOwnProperty("email")) {
            this.setState({ emailError: error.response.data["email"] });
          }
          if (error.response.data.hasOwnProperty("password")) {
            this.setState({ passwordError: error.response.data["password"] });
          }
          if (error.response.data.hasOwnProperty("re_password")) {
            this.setState({ re_passwordError: error.response.data["re_password"] });
          }
          if (error.response.data.hasOwnProperty("detail")) {
            this.setState({ status: "error" });
          }
        } else {
          this.setState({ status: "error" });
        }
      });
  };

  render() {
    let errorAlert = (
      <Alert variant="danger">
        <Alert.Heading>Problem during staff account creation</Alert.Heading>
        Please try again or contact service support for further help.
      </Alert>
    );

    let successAlert = (
      <Alert variant="success">
        <Alert.Heading>Account created</Alert.Heading>
        <p>
          We send you an email with activation link. Please check your email.
        </p>
      </Alert>
    );

    const form = (
      <div>
        <Form>
          <Form.Group controlId="firstnameId">
            <Form.Label>First name</Form.Label>
            <Form.Control
              isInvalid={this.state.first_nameError}
              type="text"
              name="first_name"
              placeholder="Enter first name"
              value={this.state.first_name}
              onChange={this.onChange}
            />
            <FormControl.Feedback type="invalid">
              {this.state.first_nameError}
            </FormControl.Feedback>
          </Form.Group>

          <Form.Group controlId="lastnameId">
            <Form.Label>Last name</Form.Label>
            <Form.Control
              isInvalid={this.state.last_nameError}
              type="text"
              name="last_name"
              placeholder="Enter last name"
              value={this.state.last_name}
              onChange={this.onChange}
            />
            <FormControl.Feedback type="invalid">
              {this.state.last_nameError}
            </FormControl.Feedback>
          </Form.Group>

          <Form.Group controlId="positionId">
            <Form.Label>Position</Form.Label>
            <Form.Control
              isInvalid={this.state.positionError}
              type="text"
              name="position"
              placeholder="Enter position in company"
              value={this.state.position}
              onChange={this.onChange}
            />
            <FormControl.Feedback type="invalid">
              {this.state.positionError}
            </FormControl.Feedback>
          </Form.Group>

          <Form.Group controlId="phone_numberId">
            <Form.Label>Phone number</Form.Label>
            <Form.Control
              isInvalid={this.state.phone_numberError}
              type="text"
              name="phone_number"
              placeholder="Enter phone number"
              value={this.state.phone_number}
              onChange={this.onChange}
            />
            <FormControl.Feedback type="invalid">
              {this.state.phone_numberError}
            </FormControl.Feedback>
          </Form.Group>

          <Form.Group controlId="usernameId">
            <Form.Label>User name</Form.Label>
            <Form.Control
              isInvalid={this.state.usernameError}
              type="text"
              name="username"
              placeholder="Enter user name"
              value={this.state.username}
              onChange={this.onChange}
            />
            <FormControl.Feedback type="invalid">
              {this.state.usernameError}
            </FormControl.Feedback>
          </Form.Group>

          <Form.Group controlId="emailId">
            <Form.Label>Your Email</Form.Label>
            <Form.Control
              isInvalid={this.state.emailError}
              type="text"
              name="email"
              placeholder="Enter email"
              value={this.state.email}
              onChange={this.onChange}
            />
            <FormControl.Feedback type="invalid">
              {this.state.emailError}
            </FormControl.Feedback>
          </Form.Group>

          <Form.Group controlId="passwordId">
            <Form.Label>Password</Form.Label>
            <Form.Control
              isInvalid={this.state.passwordError}
              type="password"
              name="password"
              placeholder="Enter password"
              value={this.password}
              onChange={this.onChange}
            />
            <Form.Control.Feedback type="invalid">
              {this.state.passwordError}
            </Form.Control.Feedback>
          </Form.Group>

          <Form.Group controlId="re_passwordId">
            <Form.Label>Password confirmation</Form.Label>
            <Form.Control
              isInvalid={this.state.re_passwordError}
              type="password"
              name="re_password"
              placeholder="Enter password again"
              value={this.state.re_password}
              onChange={this.onChange}
            />
            <Form.Control.Feedback type="invalid">
              {this.state.re_passwordError}
            </Form.Control.Feedback>
          </Form.Group>
        </Form>
        <Button color="primary" onClick={this.onSignupClick}>
          Sign up
        </Button>
      </div>
    );

    let alert = "";
    if (this.state.status === "error") {
      alert = errorAlert;
    } else if (this.state.status === "success") {
      alert = successAlert;
    }

    return (
      <Container>
        <Row>
          <Col md="6">
            <h1>Sign up</h1>
            {alert}
            {this.state.status !== "success" && form}
            <p className="mt-2">
              Already have account? <Link to="/login">Login</Link>
            </p>
          </Col>
        </Row>
      </Container>
    );
  }
}

Signup.propTypes = {};

const mapStateToProps = state => ({});

export default connect(mapStateToProps)(withRouter(Signup));
